export function getRandomName(country: string, gender: 'Masculino' | 'Feminino'): string {
    const dataset = nameDatabase[country] || fallbackNames;
    const firstNames = gender === 'Masculino' ? dataset.male : dataset.female;
    const firstName = firstNames[Math.floor(Math.random() * firstNames.length)];
    const surname = dataset.surnames[Math.floor(Math.random() * dataset.surnames.length)];
    return `${firstName} ${surname}`;
}

export function getCityNeighborhoods(city: string): CityNeighborhoodTemplate[] {
    return cityDatabase[city] || cityDatabase['Rio de Janeiro'];
}

